            // Get the text after the <strong> tag until the next <br />
            String nextSibling = strong.nextSibling() != null ? strong.nextSibling().toString().trim() : "";

            // Remove unwanted tags or trailing spaces
            nextSibling = nextSibling.replaceAll("<.*?>", "").trim();
